import React, { useState, useEffect } from "react";
import "./form.css";

const Form = () => {
  const [validEmail, SetValidEmail] = useState(false);
  const [passwordColor, SetPasswordColor] = useState('transparent');

  const isValidEmail = (email) => {
    const checkingEmail = String(email)
      .toLowerCase()
      .match(
        /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
      ); // true or false
    if (checkingEmail) {
      SetValidEmail(true);
      console.log("valid email");
    } else {
      console.log("NOT valid email");
      SetValidEmail(false);
    }
  };

  function checkPassword(e) {
    const password = e.target.value;
    let strength = 0;
    if (password.match(/[a-z]+/)) {
      strength += 1;
    }
    if (password.match(/[A-Z]+/)) {
      strength += 1;
    }
    if (password.match(/[0-9]+/)) {
      strength += 1;
    }
    if (password.match(/[$@#&!]+/)) {
      strength += 1;
    }

    if (password.length <= 8) {
        strength += 1;
    }

    switch (strength) {
      case 0:
        // strengthbar.value = 0;
        break;

      case 1:
      case 2:
        SetPasswordColor('red');
        break;

      case 3:
      case 4:
        SetPasswordColor('yellow');
        break;

      case 5:
        strengthbar.value = 100;
        break;
        default:
        break;
    }
  }

  //   const formSubmitHandler = (e) => {
  //     e.preventDefault();
  //   };

  return (
    <form>
      <div className="email_container">
        <label htmlFor="email">Email</label>
        <input type="email" id="email" onChange={isValidEmail} />
        <div className="error email_error"></div>
      </div>
      <div className="password_container">
        <label htmlFor="password">Password</label>
        <input type="password" maxLength="8" id="password" onChange={checkPassword} />
        <div className="error password_error"></div>
      </div>
      <div className="phone_container">
        <label htmlFor="phone">Phone</label>
        <input type="text" maxLength="8" id="phone" />
        <div className="error phone_error"></div>
      </div>
    </form>
  );
};

export default Form;
