import React, { useState, useEffect, useRef } from "react";
import "./form.css";

const Form = () => {
  const [validEmail, SetValidEmail] = useState(false);
  const [email, SetEmail] = useState('');
  const [validForm, SetValidForm] = useState(false);
  const [validPhoneNumber, SetValidPhoneNumber] = useState(false);
  const [passwordColor, SetPasswordColor] = useState("transparent");
  const emailRef = useRef('');

  const isValidEmail = (email) => {
      console.log(email);
    const checkingEmail = String(email)
      .toLowerCase()
      .match(
        /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
      ); // true or false
    if (checkingEmail) {
      SetValidEmail(true);
      console.log("valid email");
    } else {
      console.log("NOT valid email");
      SetValidEmail(false);
    }
  };

  function checkPassword(e) {
    const password = e.target.value;
    let strength = 0;
    if (password.match(/[a-z]+/)) {
      strength += 1;
    }
    if (password.match(/[A-Z]+/)) {
      strength += 1;
    }
    if (password.match(/[0-9]+/)) {
      strength += 1;
    }
    if (password.match(/[$@#&!]+/)) {
      strength += 1;
    }

    if (password.length > 4 && password.length <= 8 && strength === 4) {
      strength += 1;
    }
    console.log(strength);

    switch (strength) {
      case 0:
        SetPasswordColor("transparent");
        break;

      case 1:
      case 2:
        SetPasswordColor("red");
        break;

      case 3:
      case 4:
        SetPasswordColor("yellow");
        break;

      case 5:
        SetPasswordColor("#15da15");
        break;

      default:
        break;
    }
  }

    const formSubmitHandler = (e) => {
      e.preventDefault();
      isValidEmail(emailRef.current);
    };
  const isValidPhone = (e) => {
      const phoneNumber = e.target.value;
      if(phoneNumber.length < 10){
        SetValidPhoneNumber(false);
      }else{
        SetValidPhoneNumber(true);
      }   
  }


  return (
    <form onSubmit={formSubmitHandler}>
      <div className="email_container">
        <label htmlFor="email">Email</label>
        <input type="email" id="email"  ref={emailRef}/>
        <div className="error email_error"></div>
      </div>
      <div className="password_container">
        <label htmlFor="password">Password</label>
        <div className="password-color-wrapper">
          <input
            type="password"
            maxLength="8"
            id="password"
            onChange={checkPassword}
          />
          <div
            className="password_color"
            style={{ backgroundColor: passwordColor }}
          ></div>
        </div>
      </div>
      <div className="phone_container">
        <label htmlFor="phone">Phone</label>
        <input type="text" maxLength="8" id="phone" onChange={isValidPhone}/>
        <div className="error phone_error"></div>
      </div>

      <div className="submitButtonContainer">
          <input type="submit" value="submit"  className="button"/>
      </div>
    </form>
  );
};

export default Form;
